#include<bits/stdc++.h>
using namespace std;
#define ll long long

int n,m;
vector<int> Emx[1000005],Emn[1000005],E[1000005];
int fa[1000005],famx[1000005],famn[1000005];
int getfa(int x){return fa[x]==x?x:fa[x]=getfa(fa[x]);}

template<int MAXN>struct BIT{
	int c[MAXN+5];
	int N;
	void init(int _N=0){
		N=_N;
		for(int i=0;i<=N;++i)
			c[i]=0;
	}
	int lowbit(int x){return x&(-x);}
	void add(int x,int k){
		for(;x<=N;x+=lowbit(x))
			c[x]+=k;
	}
	int query(int x){
		int ret=0;
		for(;x;x-=lowbit(x))
			ret+=c[x];
		return ret;
	}
};

BIT<1000000> bit;
int dep1[1000005],dep2[1000005],dfn[1000005],sz[1000005],dcnt;
ll A,B,C,ans;
void dfs1(int x){
	dfn[x]=++dcnt;
	sz[x]=1;
	dep1[x]=dep1[famx[x]]+1;
	B+=dep1[x]-1;
	for(int y:Emx[x]){
		dfs1(y);
		sz[x]+=sz[y];
	}
}

void dfs2(int x){
	dep2[x]=dep2[famn[x]]+1;
	A+=dep2[x]-1;
	C+=bit.query(dfn[x]+sz[x]-1)-bit.query(dfn[x]-1);
	bit.add(dfn[x],1);
	for(int y:Emn[x])
		dfs2(y);
	bit.add(dfn[x],-1);
}


int main(){
	scanf("%d",&n);
	for(int i=1;i<n;++i){
		int ui,vi;
		scanf("%d %d",&ui,&vi);
		E[ui].push_back(vi);
		E[vi].push_back(ui);
	}
	for(int i=1;i<=n;++i)
		sort(E[i].begin(),E[i].end());
	for(int i=1;i<=n;++i)
		fa[i]=i;
	for(int i=1;i<=n;++i){
		for(int j:E[i]){
			if(j>i)
				continue;
			j=getfa(j);
			fa[getfa(j)]=getfa(i);
			famx[j]=i;
			Emx[i].push_back(j);
		}
	}
	for(int i=1;i<=n;++i)
		fa[i]=i;
	for(int i=n;i>=1;--i){
		for(int j:E[i]){
			if(j<i)
				continue;
			j=getfa(j);
			fa[getfa(j)]=getfa(i);
			famn[j]=i;
			Emn[i].push_back(j);
		}
	}
	bit.init(n);
	dfs1(n);
	dfs2(1);
	ans=A+B-2*C;
	printf("%lld\n",ans);
	scanf("%d",&m);
	while(m--){
		int k;
		scanf("%d",&k);
		dep2[++n]=dep2[k]+1;
		ans+=n-dep2[n];
		printf("%lld\n",ans);
	}
}
