#include<bits/stdc++.h>
using namespace std;
#define INF (1LL<<60)
#define maxn 2010
#define ll long long
int a[maxn][maxn];
ll d[maxn];
bool used[maxn];

int main()
{
    int n;
    scanf("%d",&n);
    for(int i=0;i<n;++i)
    {
        a[i][i]=0;
        for(int j=i+1;j<n;++j)
        {
            scanf("%d",&a[i][j]);
            a[j][i]=a[i][j];
        }
    }
    int mn=a[0][1];
    for(int i=0;i<n;++i)
        for(int j=i+1;j<n;++j)
            mn=min(mn,a[i][j]);
    for(int i=0;i<n;++i)
        for(int j=0;j<n;++j)
            if(i!=j) a[i][j]-=mn;
    for(int i=0;i<n;++i)
    {
        d[i]=INF; used[i]=false;
        for(int j=0;j<n;++j)
            if(i!=j)
                d[i]=min(d[i],2LL*a[i][j]);
    }
    for(int it=0;it<n;++it)
    {
        ll tmp=INF;
        int v=-1;
        for(int i=0;i<n;++i)
            if(!used[i]&&d[i]<tmp)
            {
                tmp=d[i];
                v=i;
            }
        used[v]=true;
        for(int i=0;i<n;++i)
            d[i]=min(d[i],d[v]+a[v][i]);
    }
    for(int i=0;i<n;++i)
        printf("%I64d\n",d[i]+1LL*mn*(n-1));
    return 0;
}
