//#define int long long
const int P = 1000000007; //
using namespace std;
inline void ckmax(int &a, int b) {a = max(a, b);}
inline void ckmin(int &a, int b) {a = min(a, b);}
inline void mulmod(int &a, int b) {a = 1ll * a * b % P;}
inline void addmod(int &a, int b) {int t = a + b; a = (t >= P ? t - P : t); }
inline int ksm(int a, int b) {int ans=1; for(;b;b>>=1) {if(b&1) ans=1ll*ans*a%P;a=1ll*a*a%P;}return ans;}
inline int inv(int a) {return ksm(a, P-2);}

inline void printarray(int *a, int n) {For(i, 1, n) fprintf(stderr, "%d ", a[i]); fprintf(stderr, "\n");}
namespace FastIO {
    const int SIZE=1<<16; char buf[SIZE], obuf[SIZE], str[64]; int bi=SIZE, bn=SIZE, opt;
    int read(char *s) {
        while (bn) {for (;bi<bn&&buf[bi]<=' ';bi++);if (bi<bn) break; bn=fread(buf,1,SIZE,stdin),bi=0;}
        int sn=0;while (bn) {for (;bi<bn&&buf[bi]>' ';bi++) s[sn++]=buf[bi];if (bi<bn) break; bn=fread(buf,1,SIZE,stdin),bi=0;}s[sn]=0;return sn;
    }
    bool read(int& x) {if(x)x=0;int bf=0,n=read(str); if(!n) return 0; int i=0; if (str[i]=='-') bf=1,i=1; for(x=0;i<n;i++) x=x*10+str[i]-'0'; if(bf) x=-x; return 1;}
    void write(int x) {
        if(!x) obuf[opt++] = '0'; else {if(x<0) obuf[opt++]='-',x=-x;int sn=0; while(x)str[sn++]=x%10+'0',x/=10;for (int i=sn-1;i>=0;i--) obuf[opt++]=str[i];}
        if (opt>=(SIZE>>1)){fwrite(obuf, 1, opt, stdout); opt=0;}
    }
    void write(char x) {obuf[opt++]=x;if (opt>=(SIZE>>1)){fwrite(obuf, 1, opt, stdout); opt=0;}}
    void Fflush() { if (opt) fwrite(obuf, 1, opt, stdout); opt=0;}
};
const int MAXN = 3e3 + 5;
struct Node {
	int x, y;
}a[MAXN], b[MAXN];
int n, dx[MAXN], dy[MAXN], tx, ty;
vector < int > row[MAXN], clm[MAXN];
bool cmp1(int i, int j) {return a[i].y < a[j].y;}
bool cmp2(int i, int j) {return a[i].x < a[j].x;}

int f[MAXN], siz[MAXN];
inline int find(int x) {
	while(x != f[x]) x = f[x];
	return x;
}
struct Tri {
	int x, y, r, w;
};
stack < Tri > s;
inline int merge(int x, int y, int opt = 0) {
	x = find(x), y = find(y);
	if(x == y) return 0;
	if(siz[x] < siz[y]) swap(x, y);
	if(opt) s.push((Tri) {y, f[y], x, siz[x]});
	f[y] = x; siz[x] += siz[y]; return 1;
}
inline void back() {
	while(s.size()) {
		f[s.top().x] = s.top().y; siz[s.top().r] = s.top().w;
		s.pop();
	}
}
bool check(int mid) {
//	cerr << mid << ":" << endl;
	For(i, 1, tx) row[i].clear(); tx = 0;
	For(i, 1, ty) clm[i].clear(); ty = 0;
	For(i, 1, n) a[i] = b[i];
	For(i, 1, n) dx[++tx] = a[i].x, dx[++tx] = a[i].x - mid, dx[++tx] = a[i].x + mid; 
	For(i, 1, n) dy[++ty] = a[i].y, dy[++ty] = a[i].y - mid, dy[++ty] = a[i].y + mid; 
	sort(dx + 1, dx + 1 + tx); sort(dy + 1, dy + 1 + ty); tx = unique(dx + 1, dx + 1 + tx) - dx - 1, ty = unique(dy + 1, dy + 1 + ty) - dy - 1;
//	cerr << tx << ' ' << ty << endl;
    For(i, 1, n) row[a[i].x = lower_bound(dx + 1, dx + 1 + tx, a[i].x) - dx].pb(i), clm[a[i].y = lower_bound(dy + 1, dy + 1 + ty, a[i].y) - dy].pb(i);
	For(i, 1, tx) sort(row[i].begin(), row[i].end(), cmp1);
	For(i, 1, ty) sort(clm[i].begin(), clm[i].end(), cmp2);

	For(i, 1, n + 1) f[i] = i, siz[i] = 1;
	while(s.size()) s.pop();
	For(i, 1, tx) {
		for(rint j = 1; j < row[i].size(); j++) {
			if(dy[a[row[i][j]].y] - dy[a[row[i][j-1]].y] <= mid) {
				merge(row[i][j], row[i][j-1]);
			}
		}
	}
	For(i, 1, ty) {
		for(rint j = 1; j < clm[i].size(); j++) {
			if(dx[a[clm[i][j]].x] - dx[a[clm[i][j-1]].x] <= mid) {
				merge(clm[i][j], clm[i][j-1]);
			}
		}
	}

	if(siz[find(1)] == n) return 1;
	For(x, 1, tx) {
		For(y, 1, ty) {
//			cerr << siz[find(1)] << endl;
			for(int j = 0; j < row[x].size(); j++) {
				if(abs(dy[a[row[x][j]].y] - dy[y]) <= mid) {
//					cerr << x << ' ' << y << ' ' << row[x][j] << ' ' << dy[a[row[x][j]].y]  << ' ' << dy[y] << endl;
					merge(row[x][j], n + 1, 1);
				}
			}
			for(int j = 0; j < clm[y].size(); j++) {
				if(abs(dx[a[clm[y][j]].x] - dx[x]) <= mid) {
//					cerr << x << ' ' << y << ' ' << clm[y][j] << ' ' << dx[a[clm[y][j]].x] << ' ' << dx[x] << endl;
					merge(clm[y][j], n + 1, 1);
				}
			}
//			cerr << siz[find(1)] << endl;
			if(siz[find(1)] == n + 1) return 1;
			back();
		}
	}
	return 0;
}
signed main()
{
    #ifndef ONLINE_JUDGE
        file("pro");
    #endif
    cin >> n;
    For(i, 1, n) cin >> a[i].x >> a[i].y, b[i] = a[i];

	ll l = 1, r = 2e9, res = -1;
	while(l <= r) {
//		cerr << l << ' ' << r << endl;
		int mid = (l + r) >> 1;
		if(check(mid)) {
			r = mid - 1, res = mid;
		} else l = mid + 1;
	}
	cout << res << endl;
    return FastIO::Fflush(), 0;
}
