using namespace std;

const long long P = 998244353;
int t[5100][5100];
long long a[5100];

int main() {
	//freopen("input.txt", "r", stdin);
	//freopen("output.txt", "w", stdout);

	t[1][1] = 1;
	for (int n = 2; n <= 5000; ++n) {
		for (int k = 1; k <= n; ++k) {
			t[n][k] = ((long long)(t[n - 1][k]) * (long long)(k)+(long long)(t[n - 1][k - 1]) * (long long)(n - k + 1)) % P;
		}
	}

	int n;
	cin >> n;
	a[1] = 1;
	for (int i = 2; i <= n; ++i) {
		for (int j = 1; j <= i; ++j) {
			a[j] = (a[j] * (long long)(i)) % P;
			a[j] += t[i][j];
			a[j] %= P;
		}
	}

	for (int i = 1; i <= n; ++i) {
		cout << a[i] << " ";
	}
	cout << endl;


	return 0;
}
