#include <bits/stdc++.h>

using namespace std;

const int N = 2e5 + 100;

int n;
int a[N], f, x;

int main() {
    #ifdef LOCAL
    freopen("in", "r", stdin);
    #endif

    ios_base :: sync_with_stdio(0);
    cin.tie(0);

    cin >> n;

    for (int i = 1; i <= n; ++i) {
    	cin >> a[i];
    }
    cin >> x >> f;
	long long ans = 0;	
	for (int i = 1; i <= n; ++i) {
		int l = -1, r = 1e9 + 7;
		while (r - l > 1) {
			int mid = (r + l) / 2;
			if (a[i] - mid * 1ll * (f + x) <= x)
				r = mid;
			else
				l = mid;
		}
		ans += f * r;
	}

	cout << ans;

	return 0;
}
