#include<bits/stdc++.h>
using namespace std;
int read() {
	int x=0,f=1;char c=getchar();
	while(c>'9'||c<'0') {if(c=='-')f=-1;c=getchar();}
	while(c>='0'&&c<='9') {x=x*10+c-'0';c=getchar();}
	return x*f;
}
int f[120][600],nxt[15][120][60],lst[60],n,rev[2000],fr[14][60];
int lst1[120][600],lst2[120][600],len[15],pos[15],to[15];
char S[13][120];
void Max(int &x,int y) {x=x>y?x:y;}
void print(int nw,int T) {
	if(!nw)return;
	print(lst1[nw][T],lst2[nw][T]);
	printf("%c",S[1][nw]);
}
void work() {
	n=read();
	memset(nxt,-1,sizeof(nxt));
	memset(fr,-1,sizeof(fr));
	memset(lst1,0,sizeof(lst1));
	memset(lst2,0,sizeof(lst2));
	for(int i=1;i<=n;++i) {
		scanf("%s",S[i]+1);
		len[i]=strlen(S[i]+1);
		memset(lst,-1,sizeof(lst));
		for(int j=len[i];j>=0;--j) {
			for(int c=0;c<52;++c)
				nxt[i][j][c]=lst[c];
			if(j>0)lst[rev[S[i][j]]]=j;
		}
		for(int j=1;j<=len[i];++j)
			if(fr[i][rev[S[i][j]]]==-1)fr[i][rev[S[i][j]]]=j;
	}
	memset(f,-0x3f,sizeof(f));
	int ans=0;
	for(int c=0;c<52;++c) {
		int flag=1;
		for(int i=1;i<=n;++i)if(nxt[i][0][c]==-1)flag=0;
		if(flag)f[nxt[1][0][c]][0]=1;
	}
	for(int i=1;i<=len[1];++i)
		for(int T=0;T<(1<<(n-1));++T)
			if(f[i][T]>0) {
				int nw=rev[S[1][i]];
				Max(ans,f[i][T]);
				pos[1]=i;
				for(int j=2;j<=n;++j)
					if(T>>(j-2)&1)pos[j]=nxt[j][fr[j][nw]][nw];
					else pos[j]=fr[j][nw];
				for(int c=0;c<52;++c) {
					int flag=1;
					for(int j=1;j<=n;++j) {
						to[j]=nxt[j][pos[j]][c];
						if(to[j]==-1)flag=0;
					}
					if(!flag)continue;
					int H=0;
					for(int j=2;j<=n;++j)if(to[j]!=fr[j][c])H|=(1<<(j-2));
					for(int j=1;j<=n;++j)assert(to[j]!=pos[j]);
					if(f[to[1]][H]<f[i][T]+1) {
						f[to[1]][H]=f[i][T]+1;
						lst1[to[1]][H]=i;
						lst2[to[1]][H]=T;
					}
				}
			}
	printf("%d\n",ans);
	if(ans) {
		for(int i=1;i<=len[1];++i)
			for(int S=0;S<(1<<(n-1));++S)
				if(f[i][S]==ans) {
					print(i,S);
					puts("");
					return;
				}
	}
	puts("");
}
int main() {
	for(char c='a';c<='z';++c)rev[c]=c-'a';
	for(char c='A';c<='Z';++c)rev[c]=c-'A'+26;
	int T=read();
	while(T--)work();
	return 0;
}
