#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define pb push_back
#define pii pair<int,int>
#define all(x) x.begin(), x.end()
#define rep(i, a, b) for(int i = (a); i < ((int)(b)); ++i)
#define per(i, a, b) for(int i = (a) - 1; i >= ((int)(b)); --i)
#define sz(a) ((int)a.size())
const int mod = 998244353, N = 1 << 20;

int n, a[N], b[N];

void frank() {
    cin >> n;
    for (int i = 0; i <= 20; ++i) if (n == (1 << i)) {
        n = i;
        break;
    }
    for (int i = 0; i < (1 << n); ++i) cin >> a[i];
    bool ok = 1;
    for (int i = 0; i < (1 << n); ++i) ok &= a[i] == 0;
    if (ok) {
        cout << "0\n";
        return;
    }
    int res = 0;
    for (int i = n - 1; i >= 0; --i) {
        bool ok = 1;
        for (int j = 0; j < (1 << n); ++j) b[j] = a[j] ^ a[(j + (1 << i)) % (1 << n)];
        for (int j = 0; j < (1 << n); ++j) ok &= b[j] == 0;
        if (!ok) {
            res += 1 << i;
            memcpy(a, b, sizeof b);
        }
    }
    cout << res + 1 << "\n";
}

int main () {
    ios::sync_with_stdio(false), cin.tie(0);
    int t = 1;
    //cin >> t;
    while (t--) {
        frank();
    }
}
