#include <bits/stdc++.h>
using namespace std ;

const int N = 110 ;
int v1[N], v2[N] ;
int pascal[N][3];
int ans[N], cur[N] ;

int main() {
    int n, flag= 0  ;
    cin >> n ;
    cin >> v1[n] >> v2[n] ;
    int na, nb ;
    for ( int i = 0 ; i <= n ; ++i ) {
          for ( int j = 0 ; j < min(i+1,3) ; ++j ) {
               if ( i == j || j == 0 ) pascal[i][j] = 1 ;
               else pascal[i][j] = pascal[i-1][j] + pascal[i-1][j-1] ;
          }
     }

    for ( int i = n-1 ; i > 2 ; --i ) {
        cout << "+ " << i << '\n' ;
        fflush(stdout) ;
        cin >> v1[i] >> v2[i] ;
    }

    cout << "+ 1\n" ;
    fflush(stdout) ;
    cin >> na >> nb ;
    int fr=nb ;
    if ( nb > v2[3] ) {
        cout << "+ 2\n" ;
        fflush(stdout) ;
        int a, b ;cin >> a>> b;
        v2[2] = b ;
        if ( a > na ) {
            int dif = a-na;
            for( int x = 0 ; ; ++x ) {
               if ( pascal[x][2] == dif ) {
                    ans[2] = x ; cur[2] = x+1 ;
                    break ;
               }
            }
        }
        else ans[2] = 1, cur[2] = 2 ;
        if ( na > v1[3] ) {
            int dif = na-v1[3];
            for( int x = 0 ; ; ++x ) {
               if ( pascal[x][2] == dif ) {
                    ans[1] = x ; cur[1] = x+1 ;
                    break ;
               }
            }
        }
        na = a ; nb = b;
        cout << "+ 1\n" ;
        fflush(stdout) ;
        cin >> a >> b ;
        if ( ans[1] == 0 ) {
            if ( a > na ) ans[1] = 1, cur[1] = 3;
            else ans[1] = 0, cur[1] = 2;
        }
        ans[3] = (b-nb) / cur[2] - 1 ;
        cur[3] = ans[3] + 1 ;
    }
    else {
        ans[2] = 0 ; cur[2] = 1;
        cout << "+ 2\n" ;
        fflush(stdout) ;
        cin >> v1[2] >> v2[2];

        cout << "+ 1\n" ;
        fflush(stdout) ;
        int a, b ; cin >> a >> b;
        if ( na > v1[3] ) {
            int dif = na-v1[3];
            for( int x = 0 ; ; ++x ) {
               if ( pascal[x][2] == dif ) {
                    ans[1] = x ; cur[1] = x+2 ;
                    break ;
               }
            }
        }
        else {
            if ( a > v1[2] ) ans[1] = 1, cur[1] = 3;
            else ans[1] = 0, cur[1] = 2;
        }
        ans[3] = (b-v2[2]) / cur[2] - 1 ;
        cur[3] = ans[3] + 1 ;
    }

    cur[1] = ans[1] + 1; cur[2] = ans[2] ;
    for ( int i = 4 ; i <= n ; ++i ) {
        int v = v2[i-2];
        v -= cur[i-4] * cur[i-3] ;
        v -= cur[i-3] * cur[i-1] ;
        ans[i] = (v - (i-2==2?fr:v2[i-1])) / cur[i-1] - 1 ;
//        cout << v << ' ' << v2[i-2] << ' ' << v2[i-1] << ' ' << cur[i-1] << ' ' << cur[i-2] << ' ' << cur[i-3] << ' ' << cur[i-4] << '\n' ;
        cur[i] = ans[i] + 1 ;
        cur[i-2] = ans[i-2] ;
        cur[1] = ans[1] ;
    }
    ans[n]++ ;
    cout << "! " ;
    for ( int i = 1 ; i <= n ; ++i ) cout << ans[i] << ' ' ;
    cout << '\n' ;
}
