int n,m;
int f[70][2][2][2];
int dfs(int x,int y,int z,int w)
//当前位数 超出/未超出 低位结果 进位
{
	// printf("%lld %lld %lld %lld\n",x,y,z,w);
	// fflush(stdout);
	if(x==63) 
	{
		return (y==1)&&(z==1)&&(w==0);
	}
	if(~f[x][y][z][w]) return f[x][y][z][w];
	int res=0;
	//trans:0
	res=(res+dfs(x+1,((n>>x)&1)?1:y,
	z^(((m>>x)&1)+w&1),
	((m>>x)&1)+w>1));
	//trans:1
	res=(res+dfs(x+1,((n>>x)&1)?y:0,
	z^(((m>>x)&1)+w&1),
	((m>>x)&1)+w+1>1));
	return f[x][y][z][w]=res;
}
void Meyi()
{
	m=read();
	n=read()-1;
	memset(f,-1,sizeof(f));
	printf("%lld\n",dfs(0,1,0,0));
	return ;
}
signed main()
{
	int miHoYo=read();
	while(miHoYo--) Meyi();
	return 0;
}
