#include<bits/stdc++.h>
#define rep(i,n) for (int i=1;i<=(n);i++)
using namespace std;
int n,m;
int sum,ans;
int a[505][505],b[505][505];
void solve()
{
	cin>>n>>m;rep(i,n)rep(j,m){char c;cin>>c;a[i][j]=(c=='B');}
	rep(i,n+1)rep(j,m+1)b[i][j]=a[i-1][j-1]^a[i-1][j]^a[i][j-1]^a[i][j];
	for(int i=2;i<=n+1;i++)for(int j=2;j<=m+1;j++) sum+=b[i][j];
	ans=sum;
	if(b[n+1][m+1])
	{
		for(int i=2;i<=n;i++)for(int j=2;j<=m;j++)
		{
			int tmp=sum-b[i][j]-b[i][m+1]-b[n+1][j]-b[n+1][m+1];
			tmp+=4-b[i][j]-b[i][m+1]-b[n+1][j]-b[n+1][m+1];
			ans=min(ans,tmp+3); 
		}
	}
	cout<<ans<<endl;
}
int main()
{
	solve();
	return 0;
}
