using namespace std;

int n;

int main() {
	freopen("input.txt", "r", stdin); freopen("output.txt", "w", stdout);

	scanf("%d", &n);
	int m = 0;
	while((1<<m) < n) m++;
	printf("%d\n", m);
	for(int i=0; i<m; i++) {
		int cnt = 0;
		for(int j=0; j<n; j++) if(j&(1<<i)) cnt++;
		printf("%d", cnt);
		for(int j=0; j<n; j++) if(j&(1<<i)) printf(" %d", j+1);
		printf("\n");
	}
	return 0;
}
