#include<bits/stdc++.h>
using namespace std;
const int maxn=300005;
int T,n,tot;
int a[maxn],cnt[maxn],vis[maxn];
vector< vector<int> >ans;
priority_queue< pair<int,int> >q;
int main(){
	scanf("%d",&T);
	while(T--){
		scanf("%d",&n),ans.clear();
		for(int i=1;i<=n;i++)
			cnt[i]=vis[i]=0;
		while(!q.empty())
			q.pop();
		for(int i=1;i<=n;i++)
			scanf("%d",&a[i]),cnt[a[i]]++;
		for(int i=1;i<=n;i++)
			if(cnt[i])
				q.push(make_pair(cnt[i],i));
		if(q.top().first>(n+1)/2){
			puts("NO");
			continue;
		}
		tot=0;
		for(int i=1,j;i<=n;i++)
			if(vis[i]==0){
				if(tot+1==i)
					j=i+1,ans.push_back({});
				while(!q.empty()&&q.top().first!=cnt[q.top().second])
					q.pop();
				if(q.top().first==(n-tot+1)/2)
					break;
				while(j<=n&&(vis[j]||a[i]==a[j]))
					j++;
				vis[i]=vis[j]=1,ans.back().push_back(j),ans.back().push_back(i);
				tot+=2,cnt[a[i]]--,cnt[a[j]]--,q.push(make_pair(cnt[a[i]],a[i])),q.push(make_pair(cnt[a[j]],a[j]));
			}
		int col=q.top().second;
		vector<int>u,v;
		for(int i=1;i<=n;i++)
			if(vis[i]==0)
				(a[i]==col? u:v).push_back(i);
		for(int i=0;i<u.size();i++){
			ans.back().push_back(u[i]);
			if(i<v.size())
				ans.back().push_back(v[i]);
		}
		for(int i=(int)ans.size()-2;i>=0;i--)
			if(a[ans[i].back()]==a[ans[i+1][0]])
				for(int j=0;j<ans[i].size();j+=2)
					swap(ans[i][j],ans[i][j+1]);
		puts("YES");
		for(int i=0;i<ans.size();i++)
			for(int j=0;j<ans[i].size();j++)
				printf("%d ",ans[i][j]);
		puts("");
	}
	return 0;
}
