#include <bits/stdc++.h>

using namespace std;

const int N = 2e5 + 100;

int n;
int a[N], f, x;

unordered_map <int, int> cnt;

int main() {
    #ifdef LOCAL
    freopen("in", "r", stdin);
    #endif

    ios_base :: sync_with_stdio(0);
    cin.tie(0);

    cin >> n;

    long long ans = 0;

    for (int i = 1; i <= n; ++i) {
    	string s;
    	cin >> s;
    	int c = 0;
    	for (auto it : s) {
    		c ^= (1 << (it - 'a'));
    	}
		int pairs = 0;
		pairs += cnt[c];
		for (int i = 0; i < 26; ++i) {
			c ^= (1 << i);
			pairs += cnt[c];
			c ^= (1 << i);
		}
		ans += pairs;
		++cnt[c];
    }

	cout << ans;	


	return 0;
}
