#include <cstdio>
int main()
{
    int t,a,b;
    scanf("%d",&t);
    while(t--)
    {
        scanf("%d%d",&a,&b);
        if(a==b)
            printf("0\n");
        else if(a%2==b%2&&a>b)
            printf("1\n");
        else if(a%2!=b%2&&a<b)
            printf("1\n");
        else if(a%2==b%2)
            printf("2\n");
        else printf("2\n");
    }
    return 0;
}
