#include <bits/stdc++.h>
#define dprintf(...) printf(__VA_ARGS__)
using namespace std;
typedef long long LL;

int parse(string s) {
  if (s == "M") return 0;
  int i = 0;
  while (i < s.size() && s[i] == 'X') i++;
  i++;
  if (s.back() == 'S') i *= -i;
  return i;
}

void solve() {
  string s1, s2;
  cin >> s1 >> s2;
  if (s1 == s2) {
    cout << "=\n";
  } else if (parse(s1) < parse(s2)) {
    cout << "<\n";
  } else {
    cout << ">\n";
  }
}

int main() {
  ios::sync_with_stdio(false); cin.tie(0);

  int T; cin >> T;
  for (int cs = 1; cs <= T; cs++) {
    solve();
  }
}
