#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);
	int t;
	cin >> t;
	for (; t--;) {
		int n, k, x;
		cin >> n >> k;
		bool suc = 0;
		for (int i = 0; i ^ n; ++i) {
			cin >> x;
			if (x == k)
				suc = 1;
		}
		cout << (suc ? "YES\n" : "NO\n");
	}
}
