#include <bits/stdc++.h>
#define int long long
using namespace std;
int pos[1005];
signed main(int argc, char** argv) {
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		string s;
		cin >> s;
		for(int i=0;i<s.size();i++)
			pos[s[i]]=i;
		string t;
		cin >> t;
		int ans=0;
		for(int i=1;i<t.size();i++)
			ans+=abs(pos[t[i]]-pos[t[i-1]]);
		cout << ans << "\n";
	}
	return 0;
}
