int n, k;
int a[MAXN];

bool read() {
    if (!(cin >> n >> k))
        return false;

    forn (i, n)
        cin >> a[i];

    return true;
}

void solve() {
    int l = 0, r = n - 1;

    for (; l < n && a[l] <= k; ++l);
    for (; r >= 0 && a[r] <= k; --r);

    if (l == n)
        cout << n << endl;
    else
        cout << l + n - 1 - r << endl;
}

