#include<bits/stdc++.h>

using i64 = long long;

void solve()
{
    int n;
    std::cin >> n;
    std::vector<int> a(n);
    for(int i = 0;i < n;i++) std::cin >> a[i];
    std::sort(a.begin(), a.end());
    int ans = 0;
    for(int i = 0;i < n - 1 -i;i ++) ans += a[n - 1 - i] - a[i];
    std::cout << ans << "\n";
}
int main()
{
    int t;
    std::cin >> t;
    while(t--) solve();
    return 0;
}
