#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);
	int t;
	cin >> t;
	for (; t--;) {
		int n, k;
		cin >> n >> k;
		k = min(k, 32);
		if ((1LL << k) > n) {
			cout << n + 1 << '\n';
			continue;
		}
		cout << (1LL << k) << '\n';
	}
}
