#include <bits/stdc++.h>

using namespace std;
typedef long long int lli;
typedef pair<int,int> pii;

int t, n, k;

int main()
{
	scanf("%d",&t);

	for(int p = 0 ; p < t ; p++)
	{
		scanf("%d %d",&n,&k);

		int ans = (n/k)*k;

		int remain = n - ans;
		remain = min( remain , k/2 );

		printf("%d\n",ans + remain);
	}
}
