#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef pair<ll,ll> pi;
void solve(){
	ll l,r,a; cin>>l>>r>>a;
	ll ans = r/a+r%a;
	ll down = r/a*a;
	if(l<down) down--;
	ans = max(ans,down/a + down%a);
	cout<<ans<<'\n';
}
int main(){
	ios_base::sync_with_stdio(false); cin.tie(NULL);
	int t = 1;
	cin >> t;
	while(t--) solve();
}
