#include<bits/stdc++.h>
using namespace std;

int t,n,type,x,q;
int a[100007];
long long sum;
long long cnt[2];

int main(){

    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    cin>>t;
    for(int i=1;i<=t;i++){
        cin>>n>>q;
        cnt[0]=cnt[1]=sum=0;
        for(int f=0;f<n;f++){
            cin>>a[f];
            cnt[a[f]%2]++;
            sum+=a[f];
        }

        for(int f=0;f<q;f++){
            cin>>type>>x;
            if(type==0){
                if(x%2==0){
                    sum+=cnt[0]*x;
                }else{
                    sum+=cnt[0]*x;
                    cnt[1]+=cnt[0]; cnt[0]=0;
                }
            }else{
                if(x%2==0){
                    sum+=cnt[1]*x;
                }else{
                    sum+=cnt[1]*x;
                    cnt[0]+=cnt[1]; cnt[1]=0;
                }
            }
            cout<<sum<<"\n";
        }
    }

    return 0;
}
