#include <bits/stdc++.h>

#define ll long long
#define pb push_back

using namespace std;

int main()
{
    ios_base::sync_with_stdio(0), cin.tie(0), cout.tie(0);

    ll n, x;
    cin >> n;
    vector<ll> a;
    for(int i = 0; i < n; ++i){
        cin >> x;
        a.pb(x);
    }

    for(int i = 0; i < n; ++i){
        a.pb(a[i]);
    }

    ll mx = 0, cur = 0;

    for(int i = 0; i < 2*n; ++i){
        if(a[i] == 1){
            cur++;
        }
        else{
            mx = max(mx, cur);
            cur = 0;
        }
    }

    cout << mx;

    return 0;
}
