#include <bits/stdc++.h>
#define int long long
using namespace std;
signed main(int argc, char** argv) {
	ios::sync_with_stdio(false);
	cin.tie(0);
	int T;
	cin >> T;
	while(T--)
	{
		int x,n;
		cin >> x >> n;
		int now=1;
		now=n/4*4+1;
		while(now>n) now-=4;
		while(now<=n)
		{
			if(x&1) x+=now;
			else x-=now;
			now++;
		}
		cout << x << "\n";
	}
	return 0;
}
