#include<bits/stdc++.h>
using namespace std;
bool is(vector<string> vc,char ch)
{
    for(int i=0; i<3; ++i)
    if (vc[i][0] == ch&&vc[i][1] == ch && vc[i][2] == ch)
            return true;
for (int j = 0; j < 3; j++)
        if (vc[0][j] == ch && vc[1][j] == ch && vc[2][j] == ch)
            return true;
  if (vc[0][0] == ch && vc[1][1] == ch && vc[2][2] == ch)
        return true;

    if (vc[0][2] == ch && vc[1][1] == ch && vc[2][0] == ch)
        return true;

    return false;
}

void solve()
{
   vector<string> vc(3);
   for(int i=0; i<3; ++i)
    cin>>vc[i];
    if(is(vc, 'X'))
        cout<<"X\n";
   else if (is(vc, 'O'))
        cout<<"O\n";
 else if (is(vc, '+'))
        cout<<"+\n";
else
   cout<<"DRAW\n";
}

int main()
{
    int t;
    cin>>t;
    while(t--)
    {
        solve();
    }
}
