#include<bits/stdc++.h>
#define rep(X,A,B) for(int X=A;X<=B;X++)
#define tep(X,A,B) for(int X=A;X>=B;X--)
#define LL long long
const int N=500010;
const int M=1000010;
using namespace std;

int n;

struct nn{
	int x,id;
}a[N];

int cmp(nn A,nn B){
	return A.x>B.x;
}

int main(){
	scanf("%d",&n);
	rep(i,1,n)scanf("%d",&a[i].x),a[i].id=i;
	sort(a+1,a+n+1,cmp);
	int ans=1;
	rep(i,2,n)ans+=a[i].x*(i-1)+1;
	printf("%d\n",ans);
	rep(i,1,n)printf("%d ",a[i].id);
	return 0;
}
