#include<cstdio>
int a[100000];
int main(){
    int n;
    scanf("%d",&n);
    for(int i=0;i<n;i++){
        scanf("%d",&a[i]);
    }
    for(int i=0;i<n;i++){
        for(int j=1;j<n;j++){
            if(a[i]>a[j]){
                int x=a[i];
                a[i]=a[j];
                a[j]=x;
            }
        }
    }
    int an=0;
    for(int i=1;i<n;i+=2){
        an+=a[i]-a[i-1];
    }
    printf("%d\n",an);
    return 0;
}
