using ll = long long;

const ll INF = static_cast<ll>(1e9) + 7;
const int MAXN = static_cast<int>(4e5) + 17;

int n, k;
string s;
vector<int> Q[26];
bool bad[MAXN];

bool read() {
    if (!(cin >> n >> k >> s))
        return false;

    return true;
}

void solve() {
    for (int i = n - 1; i >= 0; --i)
        Q[s[i] - 'a'].push_back(i);

    for (int j = 0; j < 26; ++j)
        for (; k > 0 && !Q[j].empty(); --k, Q[j].pop_back())
            bad[Q[j].back()] = true;

    forn (i, n)
        if (!bad[i])
            cout << s[i];

    cout << endl;
}

int main() {
    cin.tie(nullptr);
    std::ios_base::sync_with_stdio(false);
