lli power(lli a, lli n)
{
    lli res = 1;
    while (n)
    {
        if (n % 2)
        {
            res *= a;
            // res %= mod;
        }
        a *= a;
        // a %= mod;
        n /= 2;
    }
    return res;
}

lli gcd(lli a, lli b)
{
    return b ? gcd(b, a % b) : a;
}

lli lcm(lli v, lli b)
{
    return (v * b) / gcd(v, b);
}

