#include<bits/stdc++.h>
#define int long long
using namespace std;
int tt;
int n,m,k;
int vis[1000001];
int a[1000001],b[1000001];
signed main(){
	ios::sync_with_stdio(0);
	cin.tie(0); cout.tie(0);
	cin>>tt;
	while(tt--){
		cin>>n>>m>>k;
		int s1=0,s2=0;
		for(int i=1;i<=n;i++){
			cin>>a[i];
			if(!vis[a[i]]&&a[i]<=k) s1++;
			vis[a[i]]=1;
		}
		for(int i=1;i<=m;i++){
			cin>>b[i];
			if(vis[b[i]]!=2&&b[i]<=k) s2++;
			vis[b[i]]=2;
		}
		for(int i=1;i<=k;i++){
			if(!vis[i]){
				cout<<"NO\n";
				goto to;
			}
		}
		if(s1>=k/2&&s2>=k/2){
			cout<<"YES\n";
		}
		else{
			cout<<"NO\n";
		}
		to:;
		for(int i=1;i<=n;i++) vis[a[i]]=0;
		for(int i=1;i<=m;i++) vis[b[i]]=0;
	}
}
