using namespace std;
int n,x,y;
vector<int>v;
int main(){
// 	freopen("input.txt","r",stdin);
	scanf("%d%d%d",&n,&x,&y);
	if(x > y)return cout << n << endl,0;
	for(int i = 0,a;i < n;i++){
		scanf("%d",&a);
		if(a <= x)v.push_back(a);
	}
	cout << (v.size() + 1)/2;
}
