#include <iostream>
#include <vector>
#include <cstring>
#include <algorithm>
using namespace std;
int main(int argc, char** argv) {
	int T;
	cin >> T;
	while(T--)
	{
		string a,b;
		cin >> a >> b;
		int ans=1e9;
		for(int l=0;l<=20;l++)
		{
			string A="",B="";
			for(int i=0;i<(int)a.size()+1-l;i++)
			{
				A="";
				for(int j=i;j<=i+l-1;j++) A+=a[j];
				for(int i=0;i<(int)b.size()+1-l;i++)
				{
					B="";
					for(int j=i;j<=i+l-1;j++) B+=b[j];
					if(A==B) ans=min(ans,(int)a.size()+(int)b.size()-l-l);
				}
			}
		}
		cout << ans << "\n";
	}
	return 0;
}
