#include <iostream>
#include <cstdio>
#include <cstdlib>
#include <cmath>
using namespace std;
const int maxn=300010;
int n,ld=0,lcd=0,l[maxn],r[maxn],ans,rx=1000000007,rcx=1000000007;
int main()
{
	scanf("%d",&n);
	for (int i=1;i<=n;i++)
	{
		scanf("%d%d",&l[i],&r[i]);
		if (l[i]>=ld) {lcd=ld; ld=l[i];}
		 else if (l[i]>lcd) lcd=l[i];
		if (r[i]<=rx) {rcx=rx; rx=r[i];}
		 else if (r[i]<rcx) rcx=r[i];
	}
	for (int i=1;i<=n;i++)
	{
		int ll=ld,rr=rx;
		if (l[i]==ld) ll=lcd;
		if (r[i]==rx) rr=rcx;
		ans=max(ans,rr-ll);
	}
	printf("%d\n",ans);
return 0;
}
