#include <bits/stdc++.h>
using namespace std;
#define rep(i,h,t) for (int i=h;i<=t;i++)
#define dep(i,t,h) for (int i=t;i>=h;i--)
#define ll long long 
const int N=3e5;
ll a[N];
ll gcd(ll x,ll y)
{
	if (!y) return x;
	return gcd(y,x%y);
}
int main()
{
	ios::sync_with_stdio(false);
	int T;
	cin>>T;
	while (T--)
	{
		int n;
		cin>>n;
		rep(i,1,n) cin>>a[i];
		ll gg=0;
		for (int i=1;i<=n;i+=2) gg=gcd(gg,a[i]);
		bool tt=0;
		for (int i=2;i<=n;i+=2) if (a[i]%gg==0) tt=1;
		if (!tt)
		{
			cout<<gg<<endl;
			continue;
		}
		gg=0;
		for (int i=2;i<=n;i+=2) gg=gcd(gg,a[i]);
		tt=0;
		for (int i=1;i<=n;i+=2) if (a[i]%gg==0) tt=1;
		if (!tt)
		{
			cout<<gg<<endl;
			continue;
		}
		cout<<0<<endl;
	}
	return 0;
}
