#include <cstdio>
char s[200005];
long long d[200005];
long long ans[26];
int main()
{
    int t,n,m,p;
    scanf("%d",&t);
    while(t--)
    {
        scanf("%d%d",&n,&m);
        scanf("%s",s+1);
        for(int i=0;i<m;i++)
        {
            scanf("%d",&p);
            d[1]++;
            d[p+1]--;
        }
        for(int i=1;i<=n;i++)
            d[i]+=d[i-1];
        for(int i=1;i<=n;i++)
            ans[s[i]-'a']+=d[i];
        for(int i=1;i<=n;i++)
            ans[s[i]-'a']++;
        for(int i=0;i<26;i++)
            if(i!=25)
                printf("%lld ",ans[i]);
            else printf("%lld\n",ans[i]);
        for(int i=1;i<=n+1;i++)
            d[i]=0;
        for(int i=0;i<26;i++)
            ans[i]=0;
    }
    return 0;
}
