//ZJ_MRZ's Code
#include<iostream>
#include<cstdio>
#include<algorithm>
#include<cmath>
#include<cstring>
#include<string>
#include<vector>
#define re register
#define N 1010
using namespace std;
inline int read();
inline long long readl();
inline void write(int x,int ch);
inline void writel(int x,int ch);
int n,m,d,sum=0;
int c[N],ans[N],ls[N];
signed main() {
	n=read();
	m=read();
	d=read();
	for(int i=1;i<=m;i++) {
		c[i]=read();
		sum+=(c[i]-1);
	}
	ls[m]=c[m];
	for(int i=m-1;i>=1;i--)
		ls[i]=c[i]+ls[i+1];
	if(d*(m+1)<n-sum+1) {
		printf("NO\n");
	}
	else {
		printf("YES\n");
		int pla=0,ban=1;
		memset(ans,0,sizeof(ans));
		while(pla<n+1) {
			if(n+1-d-pla<=ls[ban]) {
				int lss=c[ban];
				while(pla<n+1) {
					pla++;
					if(ban<=m) 
						ans[pla]=ban;
					lss--;
					if(lss==0) {
						ban++;
						lss=c[ban];
					}
				}
				break;	
			}
			else {
				pla+=d;
				for(int i=pla;i<=pla+c[ban]-1;i++) {
					ans[i]=ban;
				}
				pla=pla+c[ban]-1;
				ban++;
			}
//			cout<<pla<<endl;
		}
		for(int i=1;i<=n;i++)
			cout<<ans[i]<<" ";
		cout<<endl;
	}
	return 0;
}
inline void writel(long long x,int ch) {
	if(x<0) {
		putchar('-');
		x=-x;
	}
	if(x>9) {
		write(x/10,0);
	}
	putchar(x%10+'0');
	if(ch)
		printf("\n");
	return;
}
inline void write(int x,int ch) {
	if(x<0) {
		putchar('-');
		x=-x;
	}
	if(x>9) {
		write(x/10,0);
	}
	putchar(x%10+'0');
	if(ch)
		printf("\n");
	return;
}
inline long long readl() {
    long long x=0,f=1;
    char ch=getchar();
    while(ch>'9'||ch<'0') {
        if(ch=='-')
            f=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9') {
        x=x*10+ch-'0';
        ch=getchar();
    }
    return x*f;
}
inline int read() {
    int x=0,f=1;
    char ch=getchar();
    while(ch>'9'||ch<'0') {
        if(ch=='-')
            f=-1;
        ch=getchar();
    }
    while(ch>='0'&&ch<='9') {
        x=x*10+ch-'0';
        ch=getchar();
    }
    return x*f;
}
