#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int a[200000];
int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);
	int t;
	cin >> t;
	for (; t--;) {
		int n, k, q;
		cin >> n >> k >> q;
		for (int i = 0; i ^ n; ++i)
			cin >> a[i];
		long long ans = 0, cnt = 0, pre = 0;
		for (int i = 0; i ^ n; ++i) {
			if (a[i] <= q) {
				if (++cnt >= k) {
					++pre;
					ans += pre;
				}
			}
			else
				cnt = pre = 0;
		}
		cout << ans << '\n';
	}
}
