using namespace std;

bool check(long long x)
{
	long long s, e;
	long long mid;

	s = 1;
	e = 10000;
	while(s <= e)
	{
		mid = (s + e) / 2;

		if (mid * mid * mid == x) return true;
		else if (mid * mid * mid < x) s = mid + 1;
		else e = mid - 1;
	}

	return false;
}

int main()
{
	int t;
	long long x;
	long long a, b;
	bool ans;

	cin >> t;
	while (t--)
	{
		cin >> x;

		ans = false;
		for (a = 1; a * a * a <= x; a++)
		{
			if (check(x - a * a * a))
			{
				ans = true;
				break;
			}
		}

		if (ans) cout << "YES\n";
		else cout << "NO\n";
	}
}
