#include<bits/stdc++.h>
using namespace std;

int t,n,last;
char w;
string s;
int maxs[2];

int main(){

    ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);

    cin>>t;
    for(int i=1;i<=t;i++){
        cin>>n>>w>>s;
        maxs[0]=maxs[1]=0;

        for(int f=0;f<s.size();f++){
            if(s[f]=='g'){last=n+f;break;}
        }
        for(int f=s.size()-1;f>=0;f--){
            if(s[f]=='g')last=f;
            if(s[f]=='r')maxs[0]=max(maxs[0],last-f);
            if(s[f]=='y')maxs[1]=max(maxs[1],last-f);
        }

        if(w=='g'){
            cout<<0<<"\n";
        }else if(w=='r'){
            cout<<maxs[0]<<"\n";
        }else{
            cout<<maxs[1]<<"\n";
        }
    }

    return 0;
}
