#include<iostream>
#include<vector>
#include<algorithm>
using namespace std;
int main() {
	ios_base::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);
	int t;
	cin >> t;
	for (; t--;) {
		long long n, k, x;
		cin >> n >> k >> x;
		long long l = k * (1 + k) / 2;
		long long r = k * (n - k + 1 + n) / 2;
		cout << (x >= l && x <= r ? "YES\n" : "NO\n");
	}
}
