#include<bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef pair<ll,ll> pi;
typedef vector<ll> V;
void solve(){
	int n,m; cin>>n>>m;
	priority_queue<V,vector<V>,greater<V>>pq;
	for(int i=1; i<=m; i++){
		ll x,w; cin>>x>>w;
		pq.push({w,x,i});
	}
	ll ans = 0;
	vector<pi>seg;
	for(int i=1; i<=2*n; i++){
		ans+=pq.top()[0];
		seg.emplace_back(pi(pq.top()[1],pq.top()[2]));
		pq.pop();
	}
	sort(seg.begin(),seg.end());
	cout<<ans<<'\n';
	int l = 0,r = seg.size()-1;
	while(l<r){
		cout<<seg[l].second<<" "<<seg[r].second<<'\n';
		l++; r--;
	}
}
int main(){
	ios_base::sync_with_stdio(false); cin.tie(NULL);
	int t = 1;
	cin >> t;
	while(t--) solve();
}
