#include<bits/stdc++.h>
using namespace std;
const int N = 200000 + 10;
typedef long long ll;
int a[N],cnt[N];
int main(){
    ios::sync_with_stdio(0);
    int n; cin>>n;
    for(int i=1;i<=n;i++) cin>>a[i], cnt[a[i]]++;
    int ans = n, t;
    for(int i=0;i<N;i++){
        if(n-cnt[i]<ans) ans = n-cnt[i], t = i;
    }
    cout<<ans<<endl;
    queue<int> q;
    for(int i=1;i<=n;i++){
        if(a[i]==t) q.push(i);
    }
    while(!q.empty()){
        t = q.front(), q.pop();
        if(t>1&&a[t-1]!=a[t]){
            if(a[t-1]>a[t]) cout<<2<<' '<<t-1<<' '<<t<<endl;
            else cout<<1<<' '<<t-1<<' '<<t<<endl;
            a[t-1] = a[t];
            q.push(t-1);
        }
        if(t<n&&a[t+1]!=a[t]){
            if(a[t+1]>a[t]) cout<<2<<' '<<t+1<<' '<<t<<endl;
            else cout<<1<<' '<<t+1<<' '<<t<<endl;
            a[t+1] = a[t];
            q.push(t+1);
        }
    }
}
