#include<bits/stdc++.h>
using namespace std;
#define int long long
const int mod=1e9+7;
int t,a[200100];
void solve(){
        int n,i,ans=0,t=-1,p=0;cin>>n;
        map<int,int>mp;
        for(i=0;i<n;i++)cin>>a[i],mp[a[i]]++;
        for(auto [f,s]:mp){
                if(t!=f-1)p=0;
                if(s>p)ans+=s-p;
                t=f,p=s;
        }
        cout<<ans<<'\n';
}
signed main(){
        ios::sync_with_stdio(false);cin.tie(nullptr);
        cin>>t;
        while(t--)solve();
        return 0;
}
