#include <bits/stdc++.h>
using namespace std;
#define ll long long
#define vll vector<ll>
#define vvll vector<vll>

int main() {
    int t;
    cin >> t;
    while (t--) {
        ll n, x, y;
        cin >> n >> x >> y;
        ll dx = n/x;
        ll dy = n/y;
        ll db = n/lcm(x,y);
        dx -= db;
        dy -= db;
        // cout << dx << ' ' << dy << ' ' << db << endl;
        ll ans = (n + n-dx+1) * dx / 2;
        ans -= (dy+1) * dy / 2;
        cout << ans << '\n';
    }
}
