#include<bits/stdc++.h>
using namespace std;



void solve()
{
  	int n, d, h;
	cin >> n >> d >> h;
	vector<int> y(n);
	for (int &i: y) 
    cin >> i;
	long double s = 0, one = 1;
	for (int i=0;i<n;++i) {
		if (i == n - 1 || y[i + 1] > y[i] + h) 
			s += d * one * h / 2;
		else
			s += (y[i + 1] - y[i]) * one * (1 + (h - y[i+1]+y[i])*one/h) * d / 2;
	}
	cout<<fixed<<setprecision(13)<<s<<"\n";
}

int main()
{
    int t;
    cin>>t;
    while(t--)
    {
        solve();
    }
}
