#include <cstdio>
#include <algorithm>
using namespace std;
int main()
{
    int t,a,b,c,ans,ansa,ansb,ansc,tmp;
    scanf("%d",&t);
    while(t--)
    {
        scanf("%d%d%d",&a,&b,&c);
        ans=0x3f3f3f3f;
        for(int i=1;i<=10000;i++)
            for(int j=i;j<=10000+i;j+=i)
                for(int k=j;k<=10000+j;k+=j)
                {
                    tmp=abs(a-i)+abs(b-j)+abs(c-k);
                    if(tmp<ans)
                    {
                        ans=tmp;
                        ansa=i;
                        ansb=j;
                        ansc=k;
                    }
                }
        printf("%d\n",ans);
        printf("%d %d %d\n",ansa,ansb,ansc);
    }
    return 0;
}
