#include <bits/stdc++.h>
using namespace std;

const int MAX_N = 1e5;
const int INF = 1e9;

int n;
char a[MAX_N + 1], b[MAX_N + 1];

bool can(char c1, char c2, char c3, char c4) {
	set<char> s;
	s.insert(c1);
	s.insert(c2);
	s.insert(c3);
	s.insert(c4);
	if (s.size() > 2) return false;
	if (s.size() == 2) {
		int cnt1 = (c1 == *s.begin()) + (c2 == *s.begin()) + (c3 == *s.begin()) + (c4 == *s.begin());
		s.erase(s.begin());
		int cnt2 = (c1 == *s.begin()) + (c2 == *s.begin()) + (c3 == *s.begin()) + (c4 == *s.begin());
		return cnt1 == cnt2;
	}
	return true;
}

int main() {
	//freopen("input.inp", "r", stdin);
	ios_base::sync_with_stdio(0); cin.tie(0);
	cin >> n;
	cin >> a + 1;
	cin >> b + 1;
	int ans = 0;
	for (int i = 1; i <= n / 2; ++i) {
		int tmp = INF;
		vector<char> cand = {a[i], a[n - i + 1], b[i], b[n - i + 1]};
		for (char c1 : cand) {
			for (char c2 : cand) {
				if (can(c1, c2, b[i], b[n - i + 1])) {
					tmp = min(tmp, (c1 != a[i]) + (c2 != a[n - i + 1]));
				}
			}
		}
		ans += tmp;
	}
	if (n % 2) {
		if (a[(n + 1) / 2] != b[(n + 1) / 2]) {
			ans++;
		}
	}
	cout << ans;
	return 0;
}
