#include<bits/stdc++.h>

using namespace std;

int64_t f[55];

int main() {
  ios_base::sync_with_stdio(0);
  cin.tie(0); cout.tie(0);
  f[0] = f[1] = 1;
  for (int i = 2; i <= 50; i++) {
    f[i] = f[i - 1] + f[i - 2];
  }
  int tt; cin >> tt;
  while (tt--) {
    int n;
    int64_t x, y;
    cin >> n >> x >> y;
    bool ok = true;
    for (int i = n; i >= 1; i--) {
      int64_t need = f[i + 1] - f[i];
      y = min(y, f[i + 1] - y + 1);
      if (y > need) {
        ok = false;
        break;
      }
      swap(x, y);
    }
    cout << (ok ? "YES" : "NO") << "\n";
  }
  return 0;
}
