#include <bits/stdc++.h>
using namespace std;

int32_t main() {
        ios_base::sync_with_stdio(0);
        cin.tie(0);
        int t;
        cin >> t;
        while (t--) {
                int n;
                cin >> n;
                vector<int> a(n);
                for (int i = 0; i < n; i++) cin >> a[i];
                sort(a.begin(), a.end());
                map<int, int> cnt;
                for (int i = 0; i < n; i++) cnt[a[i]]++;
                int64_t res = 0;
                for (auto [v, c] : cnt) {
                        res += 1ll * c * (c - 1) / 2;
                }
                res += 1ll * cnt[1] * cnt[2];
                cout << res << '\n';
        }
}
